definition.script:
pipeline {
    agent any

    tools {
        maven 'maven3'
    }
    environment {
        JAVA_HOME = "/usr/lib/jvm/java-1.8.0-openjdk-amd64"
    }

    stages {
		stage('Initialize Process') {
		    steps {
			    deleteDir()
		    }
		}
		stage('Checkout Source') {
		    steps {
			sh """
			summon --yaml \'SECURE_GITLAB_PASS: !var gitlab/password\' sh -c \'git clone http://root:\$SECURE_GITLAB_PASS@gitlab.192.168.191.138.xip.io:31080/root/SecureDemo.git .\'
			"""
		    }
		}
        stage('Parallel Tests') {
            steps {
                parallel(
                        'Unit Test by JUnit and Cobertura': {
                            sh "mvn -U clean test cobertura:cobertura -Dcobertura.report.format=xml"
                        },
                        'Quality Check by SonarQube': {
                            sh """
				summon --yaml \'SECURE_SONAR_PASS: !var sonar/password\' sh -c \'mvn sonar:sonar -Dsonar.host.url=http://cicd_sonar:9000 -Dsonar.username=admin -Dsonar.password=\${SECURE_SONAR_PASS} -Dsonar.java.binaries=target/classes\'
			    """
                        }
                )
            }
            post {
                always {
                    junit '**/target/*-reports/TEST-*.xml'
                    step([$class: 'CoberturaPublisher', coberturaReportFile: 'target/site/cobertura/coverage.xml'])
                }
            }
        }
        stage('Deploy to Artifactory') {
            steps {
		sh """
                summon --yaml \'SECURE_ARTIFACTORY_PASS: !var artifactory/password\' sh -c \'mvn deploy -DskipTests -Dartifactory_url=http://ARTIFACTORY_USER:\${SECURE_ARTIFACTORY_PASS}@ARTIFACTORY_URL/artifactory/example-repo-local -Dv=\${BUILD_NUMBER}\'
		"""
            }
        }


        stage('Trigger CD process using Ansible') {
            steps {
                sh """
                  summon --yaml "SECURE_AWX_PASS: !var awx/password" sh -c \' \\
                    curl -k -X POST \\
                        -u "AWX_USER:\$SECURE_AWX_PASS" \\
                        -H "Accept: application/json" \\
                        -H "Content-Type: application/json" \\
                        http://AWX_CONTAINER_IP:8052/api/v2/job_templates/AWX_TEMPLATE_ID/launch/ -d \\
                        "{\\"extra_vars\\": { \\"artifact_url\\":\\"http://cicd_artifactory/artifactory/example-repo-local/com/khoubyari/secure-spring-boot-rest-example/0.1.${BUILD_NUMBER}/secure-spring-boot-rest-example-0.1.${BUILD_NUMBER}.war\\"} }"  \\
                    \'
                """
            }
        }





    }
}
